package com.raydya;

import org.junit.Assert;
import org.junit.Test;

public class FindTheDifferenceTest {
    @Test
    public void test1() {
        final FindTheDifference solution = new FindTheDifference();
        final char difference = solution.findTheDifference("abcd", "abcde");

        Assert.assertEquals('e', difference);
    }

    @Test
    public void test2() {
        final FindTheDifference solution = new FindTheDifference();
        final char difference = solution.findTheDifference("", "y");

        Assert.assertEquals('y', difference);
    }

    @Test
    public void test3() {
        final FindTheDifference solution = new FindTheDifference();
        final char difference = solution.findTheDifference("a", "aa");

        Assert.assertEquals('a', difference);
    }

    @Test
    public void test4() {
        final FindTheDifference solution = new FindTheDifference();
        final char difference = solution.findTheDifference("ae", "aea");

        Assert.assertEquals('a', difference);
    }

    @Test
    public void test5() {
        final FindTheDifference solution = new FindTheDifference();
        final char difference = solution.findTheDifference(
            "",
            ""
        );

        Assert.assertEquals('t', difference);
    }
}
